        private void Jugar_inicio_Click(object sender, EventArgs e)
        {
            StartGamePanel.Visible = true;
            StartGamePanel.Dock = DockStyle.Fill;

            Partida NewGame = new Partida("Nombre generico");
            Juego.PartidaList.Insert(0, NewGame);

            //Para cada semilla se debe calcular el precio de 30 turnos hacia atrás, es decir, generar
            //una historia.
            Market market = new Market();
            foreach (Seed seed in Juego.Lista_Semillas)
            {
                List<int> seedPriceHistory = market.Price_History(seed);
                Juego.PartidaList[0].Precios_Historicos_Semillas.Add(seed.Name, seedPriceHistory);
            }

            //Al generar el mapa aleatorio, recuerde que debe mostrar dónde está la granja.
        }

        private void cargar_partida_inicio_Click(object sender, EventArgs e)
        {
            /*Cargar juego anterior: Esta opción deberá cargar un juego previo que haya sido guardado por
             * el jugador. Esta deberá cargar todos los valores guardados e iniciar en el turno que el jugador
             * grabó la partida
             */
            PanelPartidasGuardadas.Visible = true;
            PanelPartidasGuardadas.Dock = DockStyle.Fill;
            foreach (Partida partida in Juego.CargarPartidas())
            {
                int n = DataGridPartidasGuardadas.Rows.Add();
                DataGridPartidasGuardadas.Rows[n].Cells[0].Value = partida.FarmName;
                DataGridPartidasGuardadas.Rows[n].Cells[1].Value = partida.currentDateTime;
            }

            

        }

