        private void cARGARPARTIDAToolStripMenuItem_Click(object sender, EventArgs e) //Paso de  turno.
        {
            List<string> messages = Juego.Paso_de_turno();
            tURNO1ToolStripMenuItem.Text = "TURNO: " + Juego.PartidaList[0].Turn;
            dIA31ToolStripMenuItem.Text = "DIA: " + Juego.PartidaList[0].Day;

            string text = "";
            foreach (string message in messages)
            {
                text += message;
                text += Environment.NewLine;
            }

            MessageBox.Show(text, "Resultados del turno", MessageBoxButtons.OK, MessageBoxIcon.None);

            if (ProductionAdminPanel.Visible == true)
            {
                tabla_admin_ganado.Rows.Clear();
                tabla_admin_plantacion.Rows.Clear();

                foreach (Terrain ter in Juego.PartidaList[0].Map.MapaTerrenos)
                {
                    if (ter.ed != null)
                    {
                        if (ter.ed.tipo == "P")
                        {
                            Plantation p = (Plantation)ter.ed;
                            int n = tabla_admin_plantacion.Rows.Add();
                            tabla_admin_plantacion.Rows[n].Cells[0].Value = p.Name;
                            tabla_admin_plantacion.Rows[n].Cells[1].Value = p.Semilla.Name;
                            tabla_admin_plantacion.Rows[n].Cells[2].Value = p.Units;
                            tabla_admin_plantacion.Rows[n].Cells[3].Value = p.Water;
                            tabla_admin_plantacion.Rows[n].Cells[4].Value = p.Nutrients;
                            tabla_admin_plantacion.Rows[n].Cells[5].Value = p.Health;
                            tabla_admin_plantacion.Rows[n].Cells[6].Value = p.Maturity;
                            tabla_admin_plantacion.Rows[n].Cells[7].Value = p.Disease;
                            tabla_admin_plantacion.Rows[n].Cells[8].Value = p.Worms;
                            tabla_admin_plantacion.Rows[n].Cells[9].Value = p.Weeds;
                            if (p.Maturity == p.Semilla.ProductionTime)
                            {
                                tabla_admin_plantacion.Rows[n].Cells[10].Value = "LISTO";
                            }
                            else
                            {
                                tabla_admin_plantacion.Rows[n].Cells[10].Value = "NO";

                            }

                        }
                        else if (ter.ed.tipo == "C")
                        {
                            Cattle p = (Cattle)ter.ed;
                            int n = tabla_admin_ganado.Rows.Add();
                            tabla_admin_ganado.Rows[n].Cells[0].Value = p.Name;
                            tabla_admin_ganado.Rows[n].Cells[1].Value = p.Animal.Name;
                            tabla_admin_ganado.Rows[n].Cells[2].Value = p.Units;
                            tabla_admin_ganado.Rows[n].Cells[3].Value = p.Water;
                            tabla_admin_ganado.Rows[n].Cells[4].Value = p.Food;
                            tabla_admin_ganado.Rows[n].Cells[5].Value = p.Health;
                            tabla_admin_ganado.Rows[n].Cells[6].Value = p.Maturity;
                            tabla_admin_ganado.Rows[n].Cells[7].Value = p.Disease;
                            if (p.Maturity == p.Animal.ProductionTime)
                            {
                                tabla_admin_ganado.Rows[n].Cells[8].Value = "LISTO";
                            }
                            else
                            {
                                tabla_admin_ganado.Rows[n].Cells[8].Value = "NO";

                            }

                        }
                    }
                }
            }
        }










