        private void EdifOptionsComboBox_SelectedIndexChanged(object sender, EventArgs e)
        {
            destruir = false;
            EdifMarketDataGridView.Rows.Clear();
            if (EdifOptionsComboBox.Text == "Comprar plantación")
            {
                //EdifMarketDataGridView.Rows.Add("Nombre", "Tiempo de madurez", "Consumo de agua",
                // "Consumo de nutrientes", "Precio");

                foreach (Seed s in Juego.Lista_Semillas)
                {

                    int n = EdifMarketDataGridView.Rows.Add();
                    EdifMarketDataGridView.Rows[n].Cells[0].Value = s.Name;
                    EdifMarketDataGridView.Rows[n].Cells[1].Value = s.ProductionTime;
                    EdifMarketDataGridView.Rows[n].Cells[2].Value = 100;
                    EdifMarketDataGridView.Rows[n].Cells[3].Value = s.PurchasePrice;

                }
            }
            else if (EdifOptionsComboBox.Text == "Comprar ganado")
            {
                foreach (Animal a in Juego.Animal_List)
                {
                    int n = EdifMarketDataGridView.Rows.Add();
                    EdifMarketDataGridView.Rows[n].Cells[0].Value = a.Name;
                    EdifMarketDataGridView.Rows[n].Cells[1].Value = a.ProductionTime;
                    EdifMarketDataGridView.Rows[n].Cells[2].Value = a.Units;
                    EdifMarketDataGridView.Rows[n].Cells[3].Value = a.PurchasePrice;
                }
            }
            else if (EdifOptionsComboBox.Text == "Comprar almacenamiento")
            {
                foreach (StorageBuilding b in Juego.Lista_Almacenamiento)
                {
                    int n = EdifMarketDataGridView.Rows.Add();
                    EdifMarketDataGridView.Rows[n].Cells[0].Value = b.Name;
                    EdifMarketDataGridView.Rows[n].Cells[1].Value = "-";
                    EdifMarketDataGridView.Rows[n].Cells[2].Value = b.Maxcapacity + " Max.";
                    EdifMarketDataGridView.Rows[n].Cells[3].Value = b.PurchasePrice;
                }
            }
            else if (EdifOptionsComboBox.Text == "Vender/destruir edificio")
            {
                /*Vender/Destruir Edificio: Esta acción permite eliminar una edificación (plantación,
                 * ganado o bodega) de algún terreno. Dependiendo del tipo de edificación esta acción
                 * representa un costo o una ganancia. En caso de que sea plantación o ganado, se debe
                 * cobrar al jugador el costo de eliminar dicha edificación (precio de venta definido en el
                 * enunciado 2), por otro lado, si es una bodega, esta debe sumar al dinero del jugador
                 * el precio de venta de la bodega + el remate de todos los productos que esta contiene
                 * según el precio de mercado y calidad de cada uno.
                */


                DialogResult result = MessageBox.Show("Desea VENDER/DESTRUIR lo que este contenga un terreno?  (Esto le podria generar un costo)", "Aviso", MessageBoxButtons.YesNo, MessageBoxIcon.None);
                if (result == DialogResult.Yes)
                {
                    destruir = true;
                    MessageBox.Show("Seleccione el Terreno que desee", "Aviso", MessageBoxButtons.OK, MessageBoxIcon.None);

                }
            }
            else if (EdifOptionsComboBox.Text == "Vender Terreno")
            {
                DialogResult result = MessageBox.Show("Desea VENDER un TERRENO? ", "Aviso", MessageBoxButtons.YesNo, MessageBoxIcon.None);
                if (result == DialogResult.Yes)
                {
                    vender = true;
                    MessageBox.Show("Seleccione el Terreno que desee VENDER", "Aviso", MessageBoxButtons.OK, MessageBoxIcon.None);

                }
            }
        }


