        // Panel de bodegas:


        private void comboBox_bodegas_SelectedIndexChanged(object sender, EventArgs e)
        {
            foreach (StorageBuilding bodega in Juego.PartidaList[0].playerBoughtStorage)
            {
                if (comboBox_bodegas.Text == bodega.Name)
                {
                    int seedNum = 0;
                    if (bodega.ProductosGuardados.Count() != 0)
                    {
                        foreach (FinishedProduct producto in bodega.ProductosGuardados)
                        {
                            int n = StorageDataGridView.Rows.Add();
                            StorageDataGridView.Rows[n].Cells[0].Value = producto.tipo;//tipo nombre de la semilla o nombre del animal
                            StorageDataGridView.Rows[n].Cells[1].Value = producto.calidad;//calidad

                            foreach (Seed seed in Juego.Lista_Semillas)
                            {
                                if (seed.Name == producto.tipo)
                                {
                                    seedNum = Juego.Lista_Semillas.IndexOf(seed);
                                }
                            }
                            int precio_semilla = Juego.Lista_Semillas[seedNum].PurchasePrice;

                            StorageDataGridView.Rows[n].Cells[2].Value = precio_semilla * producto.calidad;//precio
                        }
                    }
                    
                }
            }

        }

        private void StorageDataGridView_CellDoubleClick(object sender, DataGridViewCellEventArgs e)
        {
            int fila = e.RowIndex;
            string nombre = StorageDataGridView.Rows[fila].Cells[0].Value.ToString();
            string calidad = StorageDataGridView.Rows[fila].Cells[1].Value.ToString();
            int precio = Convert.ToInt32(StorageDataGridView.Rows[fila].Cells[2].Value);
            foreach (StorageBuilding bodega in Juego.PartidaList[0].playerBoughtStorage)
            {
                if (bodega.Name == comboBox_bodegas.Text)
                {
                    foreach (FinishedProduct producto in bodega.ProductosGuardados)
                    {
                        if (producto.tipo == nombre && producto.calidad.ToString() == calidad)
                        {
                            //si esto se cumple hay q ver q se hace, se vende de una o se pregunta 
                            
                            DialogResult respuesta = MessageBox.Show("¿Desea vender el producto " + nombre.ToUpper() + "?", 
                                "Aviso", MessageBoxButtons.YesNo, MessageBoxIcon.None);

                            if (respuesta == DialogResult.Yes)
                            {
                                Juego.PartidaList[0].PlayerCash += precio;
                                bodega.ProductosGuardados.Remove(producto);

                                MessageBox.Show("Ha vendido el producto " + nombre.ToUpper() + "!",
                                    "Felcitaciones!", MessageBoxButtons.OK, MessageBoxIcon.None);
                            }
                        }
                    }
                }
            }


            //Actualizar el DataGridView
            StorageDataGridView.Rows.Clear();
            foreach (StorageBuilding bodega in Juego.PartidaList[0].playerBoughtStorage)
            {
                if (comboBox_bodegas.Text == bodega.Name)
                {
                    int seedNum = 0;
                    foreach (FinishedProduct producto in bodega.ProductosGuardados)
                    {
                        int n = StorageDataGridView.Rows.Add();
                        StorageDataGridView.Rows[n].Cells[0].Value = producto.tipo;//tipo nombre de la semilla o nombre del animal
                        StorageDataGridView.Rows[n].Cells[1].Value = producto.calidad;//calidad

                        foreach (Seed seed in Juego.Lista_Semillas)
                        {
                            if (seed.Name == producto.tipo)
                            {
                                seedNum = Juego.Lista_Semillas.IndexOf(seed);
                            }
                        }
                        int precio_semilla = Juego.Lista_Semillas[seedNum].PurchasePrice;

                        StorageDataGridView.Rows[n].Cells[2].Value = precio_semilla * producto.calidad;//precio
                    }
                }
            }



        }

