        private void EdifMarketDataGridView_CellDoubleClick(object sender, DataGridViewCellEventArgs e)
        {
            int fila = e.RowIndex;
            if (Juego.PartidaList[0].PlayerCash >= int.Parse(EdifMarketDataGridView.Rows[fila].Cells[3].Value.ToString()))
            {
               
                foreach (Seed semilla in Juego.Lista_Semillas)
                {
                    if (semilla.Name.ToLower() == EdifMarketDataGridView.Rows[fila].Cells[0].Value.ToString().ToLower())
                    {
                        Juego.PartidaList[0].PlayerCash -= int.Parse(EdifMarketDataGridView.Rows[fila].Cells[3].Value.ToString());
                        MessageBox.Show("Confirme sobre que terreno desea poner su plantacion de "+semilla.Name.ToUpper(), "Felicidades", MessageBoxButtons.OK, MessageBoxIcon.None);
                        semillamaster = semilla;
                        dinero50000ToolStripMenuItem.Text = "DINERO: $" + Juego.PartidaList[0].PlayerCash;
                    }
                }
                foreach (Animal animal in Juego.Animal_List)
                {
                    if (animal.Name.ToLower() == EdifMarketDataGridView.Rows[fila].Cells[0].Value.ToString().ToLower())
                    {
                        Juego.PartidaList[0].PlayerCash -= int.Parse(EdifMarketDataGridView.Rows[fila].Cells[3].Value.ToString());
                        MessageBox.Show("Confirme sobre que terreno desea poner su ganado de " + animal.Name.ToUpper(), "Felicidades", MessageBoxButtons.OK, MessageBoxIcon.None);
                        animalmaster = animal;
                        dinero50000ToolStripMenuItem.Text = "DINERO: $" + Juego.PartidaList[0].PlayerCash;
                    }
                }
                foreach(StorageBuilding bodega in Juego.Lista_Almacenamiento)
                {
                    if (bodega.Name.ToLower() == EdifMarketDataGridView.Rows[fila].Cells[0].Value.ToString().ToLower())
                    {
                        Juego.PartidaList[0].PlayerCash -= int.Parse(EdifMarketDataGridView.Rows[fila].Cells[3].Value.ToString());
                        MessageBox.Show("Confirme sobre que terreno desea poner su " + bodega.Name.ToUpper(), "Felicidades", MessageBoxButtons.OK, MessageBoxIcon.None);
                        bodegamaster = bodega;
                        dinero50000ToolStripMenuItem.Text = "DINERO: $" + Juego.PartidaList[0].PlayerCash;
                    }
                }

            }
            else
            {
                MessageBox.Show("No tienes dinero suficiente para esta operacion", "ALTO", MessageBoxButtons.OK, MessageBoxIcon.Stop);

            }

